import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import styled from 'styled-components';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import Fab from '@material-ui/core/Fab';
import NavigationIcon from '@material-ui/icons/Navigation';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import Button from '@material-ui/core/Button';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import FolderIcon from '@material-ui/icons/Folder';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

function DetailCatalog() {
  const useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
      marginTop: '90px'
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary
    },
    button: {
      margin: theme.spacing(1)
    }
  }));
  const WrapPadding = styled.div`
    padding-top: 20px;
  `;

  const WrapIcon = styled.div`
    padding: 20px;
    background-color: yellow;
  `;

  const classes = useStyles();

  const [value, setValue] = React.useState('recents');

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <div className={classes.root}>
      <Container>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <img
              src="./assets/img/image4.JPG"
              class="img-fluid"
              alt="Responsive image"
            />
          </Grid>
          <Grid item xs={6}>
            <Typography variant="h5" className="titleCatalog">
              Safina
            </Typography>
            <div className="hr"></div>
            <WrapPadding>
              <p className="price">Rp 400.000</p>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <p className="price">Warna *</p>
              <Grid container item xs={12}>
                <div className="text-center">
                  <WrapIcon>
                    <CheckCircleOutlineIcon />
                  </WrapIcon>
                </div>
              </Grid>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <Grid container item xs={12}>
                <BottomNavigation
                  value={value}
                  onChange={handleChange}
                  className={classes.root}
                >
                  <BottomNavigationAction
                    label="Recents"
                    value="recents"
                    icon={<RestoreIcon />}
                  />
                  <BottomNavigationAction
                    label="Favorites"
                    value="favorites"
                    icon={<FavoriteIcon />}
                  />
                  <BottomNavigationAction
                    label="Nearby"
                    value="nearby"
                    icon={<LocationOnIcon />}
                  />
                  <BottomNavigationAction
                    label="Folder"
                    value="folder"
                    icon={<FolderIcon />}
                  />
                </BottomNavigation>
                <Button
                  variant="contained"
                  color="primary"
                  className={classes.button}
                  startIcon={<AddShoppingCartIcon />}
                >
                  ADD TO CART
                </Button>
              </Grid>
            </WrapPadding>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default DetailCatalog;
