import { createStore } from 'redux';
import { Provider } from 'react-redux';
import Routers from './components/router/Routers';

// this is global state
const globalState = {
  totalOrder: 0
};

// reducer
const rootReducer = (state = globalState, action) => {
  return state;
};

// store
