import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import styled from 'styled-components';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import IndeterminateCheckBoxIcon from '@material-ui/icons/IndeterminateCheckBox';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    marginTop: '90px'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary
  },
  button: {
    margin: theme.spacing(1)
  }
}));
const WrapPadding = styled.div`
  padding-top: 20px;
`;

const WrapIcon = styled.div`
  padding: 20px;
  background-color: yellow;
`;

const WrapValue = styled.div`
  padding-top: 18px;
`;

function DetailCatalog() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Container>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <img
              src="./assets/img/image4.JPG"
              class="img-fluid"
              alt="Responsive image"
            />
          </Grid>
          <Grid item xs={6}>
            <Typography variant="h5" className="titleCatalog">
              Safina
            </Typography>
            <div className="hr"></div>
            <WrapPadding>
              <p className="price">Rp 400.000</p>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <p className="price">Warna *</p>
              <Grid container item xs={12}>
                <div className="text-center">
                  <WrapIcon>
                    <CheckCircleOutlineIcon />
                  </WrapIcon>
                </div>
              </Grid>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <Grid container item xs={12}>
                {{ addCart }}
              </Grid>
            </WrapPadding>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

const addCart = () => {
  const classes = useStyles();
  return (
    <div>
      <IconButton
        className={classes.button}
        aria-label="minus"
        color="secondary"
        size="large"
      >
        <IndeterminateCheckBoxIcon />
      </IconButton>
      <WrapValue>
        <Typography>0</Typography>
      </WrapValue>
      <IconButton className={classes.button} aria-label="add" color="primary">
        <AddCircleIcon />
      </IconButton>
      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        startIcon={<AddShoppingCartIcon />}
      >
        ADD TO CART
      </Button>
    </div>
  );
};

export default DetailCatalog;
