import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import styled from 'styled-components';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import IndeterminateCheckBoxIcon from '@material-ui/icons/IndeterminateCheckBox';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import InstagramIcon from '@material-ui/icons/Instagram';
import FacebookIcon from '@material-ui/icons/Facebook';
import WhatsAppIcon from '@material-ui/icons/WhatsApp';

const imgDetail = './assets/img/catalog/left1.jpeg';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    marginTop: '90px'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary
  },
  button: {
    margin: theme.spacing(1)
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary
  }
}));
const WrapPadding = styled.div`
  padding-top: 20px;
`;

const WrapIcon = styled.div`
  padding: 20px;
  background-color: yellow;
`;

const WrapValue = styled.div`
  padding-top: 18px;
`;

function DetailCatalog() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Container>
        <Grid container spacing={2}>
          <Grid item xs={6} className="text-center">
            <figure class="figure">
              <img
                src={imgDetail}
                class="figure-img img-fluid rounded"
                alt="..."
                width="500"
                // height="300"
              />
            </figure>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="h5" className="titleCatalog">
              Safina
            </Typography>
            <div className="hr"></div>
            <WrapPadding>
              <p className="price">Rp 65.000</p>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <p className="price">Warna *</p>
              <Grid container item xs={12}>
                <div className="text-center">
                  <WrapIcon>
                    <CheckCircleOutlineIcon />
                  </WrapIcon>
                </div>
              </Grid>
            </WrapPadding>
            <hr />
            <WrapPadding>
              <AddCart />
            </WrapPadding>
            <hr />
            <WrapPadding>
              <Panels />
            </WrapPadding>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

const AddCart = () => {
  const classes = useStyles();
  const [count, setCount] = useState(0);

  handleMinus = () => {};

  return (
    <div>
      <Grid container item xs={12}>
        <IconButton
          className={classes.button}
          aria-label="minus"
          color="secondary"
          size="large"
          onClick={this.handleMinus}
        >
          <IndeterminateCheckBoxIcon />
        </IconButton>
        <WrapValue>
          <Typography>{count}</Typography>
        </WrapValue>
        <IconButton className={classes.button} aria-label="add" color="primary">
          <AddCircleIcon />
        </IconButton>
        <Button
          variant="contained"
          color="primary"
          className={classes.button}
          startIcon={<AddShoppingCartIcon />}
        >
          ADD TO CART
        </Button>
      </Grid>
    </div>
  );
};

const Panels = () => {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleChange = panel => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };
  return (
    <div>
      <ExpansionPanel
        expanded={expanded === 'panel1'}
        onChange={handleChange('panel1')}
      >
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1bh-content"
          id="panel1bh-header"
        >
          <Typography className={classes.heading}>Informasi Produk</Typography>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
          <Typography>
            Size : 120x120cm (segiempat)
            <br />
            <br />
            Bahan : Voal Waffle (tekstur kotak-kotak seperti waffle & sedikit
            bergelombang,ringan,mudah dibentuk,tegap di dahi,ironless dan tidak
            terawang).
            <br />
            <br />
            Detail : jahit tepi rapih
          </Typography>
        </ExpansionPanelDetails>
      </ExpansionPanel>
      <ExpansionPanel
        expanded={expanded === 'panel2'}
        onChange={handleChange('panel2')}
      >
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel2bh-content"
          id="panel2bh-header"
        >
          <Typography className={classes.heading}>Bagikan</Typography>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
          <Typography>
            <a href="https://www.instagram.com/swd.official/">
              <InstagramIcon />
            </a>
            <a href="#">
              <FacebookIcon />
            </a>
          </Typography>
        </ExpansionPanelDetails>
      </ExpansionPanel>
      <ExpansionPanel
        expanded={expanded === 'panel3'}
        onChange={handleChange('panel3')}
      >
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel3bh-content"
          id="panel3bh-header"
        >
          <Typography className={classes.heading}>Bantuan</Typography>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
          <Typography>
            <WhatsAppIcon style={{ color: ' green' }} />
            +62 851 2345 678
          </Typography>
        </ExpansionPanelDetails>
      </ExpansionPanel>
    </div>
  );
};

export default DetailCatalog;
