import React, { createContext, useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Home from '../home/Home';
import FormHome from '../home/FormHome';
import NavBar from '../navBar/NavBar';
import Footer from '../footer/Footer';
import DetailItem from '../detailItem/DetailItem';

export const RootContext = createContext();
const Provider = RootContext.Provider;

export default function Routers() {
  const [order, setOrder] = useState(10);

  const dispatch = action => {
    if (action.type === 'PLUS') {
      return setOrder(order + 1);
    }
    if (action.type === 'MINUS') {
      return setOrder(order - 1);
    }
  };

  return (
    <Router>
      <Provider value={(order, dispatch)}>
        <Route exact path="/" component={Home} />
        <Route path="/nav-bar" component={NavBar} />
        <Route path="/form-home" component={FormHome} />
        <Route path="/footer" component={Footer} />
        <Route path="/detailitem" component={DetailItem} />
      </Provider>
    </Router>
  );
}
